import { Component, OnInit } from '@angular/core';
import { catchError, empty, Observable, Subject } from 'rxjs';
import { Agenda } from './agenda';
import { AgendaService } from './agenda.service';

@Component({
  selector: 'app-agenda',
  templateUrl: './agenda.component.html',
  styleUrls: ['./agenda.component.css']
})

export class AgendaComponent implements OnInit {

  constructor(private _agendaService: AgendaService) { }

  agenda$!: Observable<Agenda[]>;
  error$ = new Subject<boolean>();

  ngOnInit() {
    this.agenda$ =  this._agendaService.list()
    .pipe(
      catchError(error => {
        console.error(error);
        this.error$.next(true);
        return empty();
      })
    );
  }
}
<div class="container-lg">
    <div class="opcoes">
        <button type="button" class="btn btn-primary btn-sm" routerLink="novo">Novo registro</button>
    </div>

    <div class="listagem">
        <table class="table table-striped table-hover" *ngIf="agenda$ | async as agenda; else loadingError">
            <thead>
                <tr>
                    <th>Registro</th>
                    <th>Nome</th>
                    <th>Telefone</th>
                    <th>E-mail</th>
                    <th>Empresa</th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
            <tr *ngFor="let item of agenda">
                <td>{{item.id}}</td>
                <td>{{item.nome}}</td>
                <td>{{item.telefone}}</td>
                <td>{{item.email}}</td>
                <td>{{item.empresa}}</td>
                <td>
                    <span>
                        <button type="button" class="btn btn-warning btn-sm">Atualizar</button>
                        {{''}}
                        <button type="button" class="btn btn-danger btn-sm">Deletar</button>
                    </span>
                </td>
            </tr>
            </tbody>
        </table>

        <ng-template #loadingError>
            <div *ngIf="error$ | async; else loading" >
                Erro ao carregar agenda, tente novamente mais tarde...
            </div>
            <ng-template #loading>
                <span>Carregando agenda...</span>
            </ng-template>
        </ng-template>
    </div>
</div>
