import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { AgendaService } from '../crud/agenda.service';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { HttpParams } from '@angular/common/http';
import { map } from 'rxjs';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

  form!: FormGroup;
  submited = false;

  constructor(
    private fb: FormBuilder, 
    private service: AgendaService, 
    private location: Location, 
    private route: ActivatedRoute
    ) {}

  ngOnInit() {
    this.route.params
    .pipe(
      map((params: any) => params['id']),
      switchMap()
    ) 
    .subscribe(
      (id) => {
        const agenda$ = this.service.loadById(id);
        agenda$.subscribe((agenda: any) => {
          this.updateForm(agenda);
        });
      }
    );

    this.form = this.fb.group({
      id: [null],
      nome: [null],
      telefone: [null],
      email : [null],
      empresa: [null],
    });
  }

  updateForm(agenda: {
    telefone: any;
    email: any;
    empresa: any;
    id: any; 
    nome: any; 
    }){
    this.form.patchValue({
      id: agenda.id,
      nome: agenda.nome,
      telefone: agenda.telefone,
      email: agenda.email,
      empresa: agenda.empresa
    });
  }

  onSubmit(){
    this.submited = true;
    if (this.form.valid) {
      this.service.create(this.form.value).subscribe(
        success => {
          alert("Contato salvo com sucesso!");
          this.location.back();
        },
      );
    }
  }

  onCancel(){
    this.submited = false;
    this.form.reset();
  }
}

function switchMap(): import("rxjs").OperatorFunction<any, unknown> {
  throw new Error('Function not implemented.');
}
