import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { AgendaService } from '../crud/agenda.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

  form!: FormGroup;
  submited = false;

  constructor(private fb: FormBuilder, private service: AgendaService, private location: Location) {}

  ngOnInit() {
    this.form = this.fb.group({
      nome: [null],
      telefone: [null],
      email : [null],
      empresa: [null],
    });
  }

  onSubmit(){
    this.submited = true;
    console.log(this.form.value);
    if (this.form.valid) {
      console.log('submit');
      this.service.create(this.form.value).subscribe(
        success => alert("Contato salvo com sucesso!"),
      );
    }
  }

  onCancel(){
    this.submited = false;
    this.form.reset();
  }
}
